import React, { useState } from 'react';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="App">
      <h1>IS215 - Advanced Computer Systems</h1>
      <h2>Group C - Final Project</h2>

      {/* Upload Section */}
      <div className="container upload-section">
        <label htmlFor="imageUpload" className="upload-btn">
          Upload Image
        </label>
        <input
          type="file"
          id="imageUpload"
          accept="image/*"
          onChange={handleImageUpload}
          style={{ display: 'none' }}
        />
      </div>

      {/* Image Preview Section */}
      <div className="container">
        <div className="label">Image Preview</div>
        {preview && <img id="imagePreview" src={preview} alt="Preview" />}
      </div>

      {/* News Article Section */}
      <div className="container news-article">
        <div className="label">News Article</div>
        <p id="newsContent">
          {image
            ? `This is a dynamically loaded news article based on "${image.name}".`
            : 'Upload an image to view a related news article here.'}
        </p>
      </div>
    </div>
  );
}

export default App;
